import React, { useState,useEffect} from 'react'
import { Link } from 'react-router-dom';
import BookService from '../services/BookService'

const AddBooksComponent = () => {
const [book,setBook]=useState([])
useEffect(()=>{
    BookService.getAllBooks().then((response)=>{
        setBook(response.data);
console.log(response.data);
    }).catch(error=>{
        console.log(error);
    })

},[])
  return (
    <div className='container '>
         <Link to="/add-book" className='btn btn-primary me-5 my-3'>Add book</Link>
         <Link to="/borrow-book" className='btn btn-primary me-5 my-3'>Add book</Link>
      <h2 className="text-center"></h2>
      <table className='table table-bordered table-striped'>
<thead>
    <th> ISBN</th>
    <th>Title</th>
    <th>Author Name</th>
    <th>Borrowed</th>
    <th>Borrowed By</th>
</thead>
<tbody>
    {
        book.map(
            book =>
            <tr key={book.id}>
                <td>{book.id}</td>
                <td>{book.title}</td>
                <td>{book.author}</td>
                <td>{book.borrowed}</td>
                <td>{book.borrowedBy}</td>
            </tr>
        )
    }
</tbody>
      </table>
      
     
    
    </div>
  )

}

export default AddBooksComponent
