import './App.css';
import React, { useState } from 'react'
import AddUsersComponent from './components/AddUsersComponent';
import Navbar from './components/Navbar';
function App() {
  const toggleMode=()=>{
    if(mode==='light'){
     setMode('dark')
     setColor('off');
      document.body.style.backgroundColor='#042743'


    }
    else{

      setMode('light')
      setColor('off')
      document.body.style.backgroundColor='#b1c2a1'
      showAlert("Light mode has been enabled","success");
    }
  }
  const[mode,setMode]=useState('light');

  const[color,setColor]=useState('off');

  const changeColor=()=>{
    if(mode==='light'){

      if(color==='off'){
        setColor('on')
        document.body.style.backgroundColor='#e6e2cc';
      }
      else{
        setColor('off')
        document.body.style.backgroundColor='#b1c2a1';
      }
    }
    else{
      if(color==='off'){
        setColor('on')
        document.body.style.backgroundColor='#103b4f';
      }
      else{
        setColor('off')
        document.body.style.backgroundColor='#042743';
      }


    }
  }

  return (

    <div>

<Navbar title="TextUtils" mode={mode} toggleMode={toggleMode} color={color} changeColor={changeColor} />
    <AddUsersComponent/>
    </div>
  );
}

export default App;
