import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import BookService from '../services/BookService';

const BorrowBookComponent = () => {
    const [bookId, setBookId] = useState('');
    const [userId, setUserId] = useState('');
    const navigate = useNavigate();

    const borrowBook = (e) => {
        e.preventDefault();
        BookService.borrowBook(bookId, userId).then((response) => {
            console.log(response.data);
            navigate('/bookInfo');
        }).catch(error => {
            console.log(error);
        });
    };

    return (
        <div className='container'>
            <div className="row">
                <div className='card col-md-6 offset-md-3 offset-md-3 my-5'>
                    <h2 className='text-center'>Borrow Book</h2>
                    <div className='card-body'>
                        <form>
                            <div className='form-group mb-2'>
                                <label className='form-label'>Book ID:</label>
                                <input
                                    type='text'
                                    placeholder='Enter Book ID'
                                    name='bookId'
                                    className='form-control'
                                    value={bookId}
                                    onChange={(e) => setBookId(e.target.value)}
                                />
                            </div>
                            <div className='form-group mb-2'>
                                <label className='form-label'>User ID:</label>
                                <input
                                    type='text'
                                    placeholder='Enter User ID'
                                    name='userId'
                                    className='form-control'
                                    value={userId}
                                    onChange={(e) => setUserId(e.target.value)}
                                />
                            </div>
                            <button className='btn btn-success' onClick={borrowBook}>Borrow</button>
                            <button className='btn btn-danger ms-5' onClick={() => navigate('/bookInfo')}>Cancel</button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default BorrowBookComponent;
