import React, { useState, useEffect } from 'react';
import BookService from '../services/BookService';

const BorrowBookComponent = ({ bookId }) => {
  const [userId, setUserId] = useState('');
  const [bookDetails, setBookDetails] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    // Fetch book details on component mount
    BookService.getAllBooks()
      .then(response => {
        const book = response.data.find(b => b.id === bookId);
        setBookDetails(book);
      })
      .catch(error => {
        console.error('Error fetching book details:', error);
        setError('Failed to fetch book details.');
      });
  }, [bookId]);

  const handleBorrow = () => {
    if (!userId) {
      setError('Please enter a user ID.');
      return;
    }

    BookService.borrowBook(bookId, userId)
      .then(response => {
        setBookDetails(response.data);
        setError('');
        alert('Book borrowed successfully');
      })
      .catch(error => {
        console.error('Error borrowing book:', error);
        setError('Invalid user ID or other error occurred.');
        alert('Error: Invalid user ID or other error occurred.');
      });
  };

  if (!bookDetails) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>{bookDetails.title}</h2>
      <p>{bookDetails.author}</p>
      <p>Status: {bookDetails.borrowed ? 'Borrowed' : 'Available'}</p>
      {!bookDetails.borrowed && (
        <div>
          <input
            type="text"
            placeholder="Enter User ID"
            value={userId}
            onChange={(e) => setUserId(e.target.value)}
          />
          <button onClick={handleBorrow}>Borrow Book</button>
        </div>
      )}
      {bookDetails.borrowed && <p>This book is already borrowed.</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
};

export default BorrowBookComponent;
