import React, { useState } from 'react';
import BookService from '../services/BookService';
import { useNavigate, useParams } from 'react-router-dom';

const BorrowBookComponent = () => {
    const { bookId } = useParams();
    const [userId, setUserId] = useState('');
    const navigate = useNavigate();

    const handleBorrow = () => {
        BookService.borrowBook(bookId, userId)
            .then(response => {
                console.log('Book borrowed:', response.data);
                navigate('/bookInfo');
            })
            .catch(error => {
                console.error('Error borrowing book:', error);
            });
    };

    return (
        <div className="container">
            <h2 className="text-center">Borrow Book</h2>
            <div className="card col-md-6 offset-md-3 offset-md-3">
                <div className="card-body">
                    <form>
                        <div className="form-group mb-2">
                            <label className="form-label">User ID:</label>
                            <input 
                                type="text" 
                                placeholder="Enter User ID" 
                                name="userId" 
                                className="form-control"
                                value={userId}
                                onChange={e => setUserId(e.target.value)} 
                            />
                        </div>
                        <button type="button" className="btn btn-success" onClick={handleBorrow}>Borrow</button>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default BorrowBookComponent;
