import React, { useState,useEffect,Link} from 'react'
import StudentService from '../services/StudentService'

const AddUsersComponent = () => {
const [student,setStudent]=useState([])
useEffect(()=>{
    StudentService.getAllStudents().then((response)=>{
        setStudent(response.data);
console.log(response.data);
    }).catch(error=>{
        console.log(error);
    })

},[])
  return (
    <div className='container'>
      <h2 className="text-center"></h2>
      <table className='table table-bordered table-striped'>
<thead>
    <th>Student Id</th>
    <th>Student Name</th>
    <th>Mobile No.</th>
</thead>
<tbody>
    {
        student.map(
            student =>
            <tr key={student.id}>
                <td>{student.id}</td>
                <td>{student.name}</td>
                <td>{student.mobile_no}</td>
            </tr>
        )
    }
</tbody>
      </table>
      <Router>
    <div>
        
<Navbar title="Library Management System"  />

   
     <Routes>
  

     <Route path="studentInfo" element={<AddUsersComponent/>}/>
     <Route path="studentInfo" element={<AddUsersComponent/>}/>
    
    </Routes>
    </div>
    </Router>
      <Link to="/add-student" className='btn btn-primary me-5'>Add Student</Link>
      <Link to="/delete-student" className='btn btn-primary me-5'>Delete StudentInfo</Link>
    
    </div>
  )
}

export default AddUsersComponent
