/*
import React, { useState,useEffect} from 'react'
import { Link } from 'react-router-dom';
import BookService from '../services/BookService'

const AddBooksComponent = () => {
const [book,setBook]=useState([])
useEffect(()=>{
    BookService.getAllBooks().then((response)=>{
        setBook(response.data);
console.log(response.data);
    }).catch(error=>{
        console.log(error);
    })

},[])
  return (
    <div className='container '>
         <Link to="/add-book" className='btn btn-primary me-5 my-3'>Add book</Link>
         <Link to="/borrow-book" className='btn btn-primary me-5 my-3'>Borrow Book</Link>
      <h2 className="text-center"></h2>
      <table className='table table-bordered table-striped'>
<thead>
    
    <th>Title</th>
    <th>Author Name</th>
    <th>ISBN No.</th>
    <th>Borrowed</th>
    <th>Borrowed By</th>
    <th>Actions</th>

</thead>
<tbody>
    {
        book.map(
            book =>
            <tr key={book.id}>
             
                <td>{book.title}</td>
                <td>{book.author}</td>
                <td>{book.isbn}</td>
                <td>{book.borrowed}</td>
                <td>{book.borrowedBy}</td>
                <td><Link className="btn btn-info" to={`/edit-book/${book.id}`}>Update</Link></td>
            </tr>
        )
    }
</tbody>
      </table>
      
     
    
    </div>
  )

}

export default AddBooksComponent*/
